import { useState } from "react";
import { patients } from "../data/mockData.js";
import { mockPatients } from "../data/mockData"; // Correct named import

const filteredPatients = mockPatients.filter(patient => patient.diagnosis === 'Flu');
console.log(filteredPatients); // Example to check filtered data



const PatientSearch = () => {
  const [query, setQuery] = useState("");

  const filtered = patients.filter(p =>
    p.name.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <div className="container mt-4">
      <h2>Search Patients</h2>
      <input
        type="text"
        placeholder="Enter name..."
        className="form-control my-3"
        onChange={(e) => setQuery(e.target.value)}
      />
      <ul className="list-group">
        {filtered.map((p, i) => (
          <li key={i} className="list-group-item">
            {p.name} â€“ {p.status}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PatientSearch;
